import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';

const ClaimDetails = () => {
  const { id } = useParams();
  const history = useHistory();
  const [claim, setClaim] = useState({});
  const [status, setStatus] = useState('');
  const [remarks, setRemarks] = useState('');

  useEffect(() => {
    if (id) {
      console.log(`Fetching claim details for ID: ${id}`);
      axios.get(`http://localhost:8080/admin/claims/${id}/approve`, {
        headers: {
          'Authorization': 'Basic YWRtaW4xMjM6Y21z'
        }
      })
      .then(response => {
        setClaim(response.data);
        setStatus(response.data.status);
        setRemarks(response.data.adminRemarks);
      })
      .catch(error => {
        console.error('There was an error fetching the claim!', error);
      });
    } else {
      console.error('ID is undefined');
    }
  }, [id]);

  const handleUpdate = () => {
    if (id) {
      console.log(`Updating claim with ID: ${id}, status: ${status}, remarks: ${remarks}`);
      axios.post(`http://localhost:8080/admin/claims/${id}/approve?remarks=${remarks}&isApproved=${status === 'Approved'}`, {}, {
        headers: {
          'Authorization': 'Basic YWRtaW4xMjM6Y21z'
        }
      })
      .then(() => {
        history.push('/claimdetails/${claim.id}/approve');
      })
      .catch(error => {
        console.error('There was an error updating the claim!', error);
      });
    } else {
      console.error('ID is undefined');
    }
  };

  return (
    <div>
      <h1>Claim Detail</h1>
      <p>Email: {claim.userEmail}</p>
      <p>Amount Claimed: {claim.amountClaimed}</p>
      <p>Policy Name: {claim.policyName}</p>
      <div>
        <label>
          Status:
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="1">Approved</option>
            <option value="0">Denied</option>
          </select>
        </label>
      </div>
      <div>
        <label>
          Remarks:
          <textarea value={remarks} onChange={(e) => setRemarks(e.target.value)} />
        </label>
      </div>
      <button onClick={handleUpdate}>Update</button>
    </div>
  );
}

export default ClaimDetails;
