import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './PolicyList.css';

const UserList = () => {
  const [Userclaims, setUserclaims] = useState([]);
  const [expandedPolicyId, setExpandedPolicyId] = useState(null);
  const email = localStorage.getItem('email');

  useEffect(() => {
    axios.get('http://localhost:8080/claim/1', {
      headers: {
        'Authorization': 'Basic VGVhbTE6VGVhbTE='
      }
    })
    .then(response => {
      setUserclaims(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the Userclaims!', error);
    });
  }, []);

  const toggleDescription = (policyId) => {
    setExpandedPolicyId(expandedPolicyId === policyId ? null : policyId);
  };

  return (
    <div className='policy-list'>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Policy Name</th>
            <th>Coverage Amount</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {Userclaims.map(policy => (
            <React.Fragment key={policy.id}>
              <tr onClick={() => toggleDescription(policy.id)} style={{ cursor: 'pointer' }}>
                <td>{policy.id}</td>
                <td>{policy.amountClaimed}</td>
                <td>{policy.claimDate}</td>
                <td>
                  {expandedPolicyId === policy.id ? (
                    <div>
                      <p>{policy.adminRemarks}</p>
                    </div>
                  ) : (
                    <span>
                      {policy.adminRemarks.length > 100 ? 
                        `${policy.adminRemarks.substring(0, 100)}... ` : policy.adminRemarks}
                      <span className="more-link" onClick={() => toggleDescription(policy.id)}>
                      </span>
                    </span>
                  )}
                </td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
