import axios from 'axios';
import React, { useState } from 'react';
import './Signin.css';

function Signin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password || !role) {
      alert('Please fill in all fields');
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8080/login?email=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`);

      if (response.status === 200) {
        // Extract the user_id from the response string
        const responseData = response.data;
        const userIdString = responseData.match(/Login successful! (\d+)/)[1];
        const userId = parseInt(userIdString, 10);

        // Store user_id and email in local storage
        localStorage.setItem('user_id', userId);
        localStorage.setItem('email', username);

        // Redirect based on role
        let redirectUrl = '#';

        if (role === 'Customer') {
          redirectUrl = '/emply';
        } else if (role === 'Hospital TPA') {
          redirectUrl = '/hospital-tpa';
        } else if (role === 'Admin') {
          redirectUrl = '/admin';
        }

        window.location.href = redirectUrl;
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        alert('Invalid username or password');
      } else {
        alert('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div className='bg'>
      <div className='wrapper'>
        <video src='/videos/video-7.mp4' autoPlay loop muted />
        <div className='dual'>
          <div className='bg-img'></div>
          <div className='wrapper1'>
            <form onSubmit={handleLogin}>
              <h1>Welcome Back!</h1>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="Email"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <select 
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  required
                >
                  <option value='' disabled>Select Role</option>
                  <option value="Customer">Customer</option>
                  <option value="Hospital TPA">Hospital TPA</option>
                  <option value="Admin">Admin</option>
                </select>
              </div>
              <div className="remember-forgot">
                <a href="/forgot">Forgot password?</a>
              </div>
              <button type='submit'>Login</button>
              <div className="register-link">
                <p>Don't have an account?<a href="/sign-up">Register</a></p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signin;
