import React, { useState } from 'react';
import './Forgt_ps.css'; // Ensure this is the correct path to your CSS file

const EmailStep = ({ email, setEmail, handleSendEmail }) => (
  <div className="step-container">
    <input
      type="email"
      placeholder="Enter your email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
      required
    />
    <button onClick={handleSendEmail}>Send Email</button>
  </div>
);

const TokenStep = ({ token, setToken, handleVerifyToken }) => (
  <div className="step-container">
    <input
      type="text"
      placeholder="Enter the token"
      value={token}
      onChange={(e) => setToken(e.target.value)}
      required
    />
    <button onClick={handleVerifyToken}>Verify Token</button>
  </div>
);

const PasswordResetStep = ({
  newPassword,
  setNewPassword,
  confirmPassword,
  setConfirmPassword,
  handleResetPassword,
}) => (
  <div className="step-container">
    <input
      type="password"
      placeholder="New password"
      value={newPassword}
      onChange={(e) => setNewPassword(e.target.value)}
      required
    />
    <input
      type="password"
      placeholder="Confirm new password"
      value={confirmPassword}
      onChange={(e) => setConfirmPassword(e.target.value)}
      required
    />
    <button onClick={handleResetPassword}>Reset Password</button>
  </div>
);

const ForgotPassword = () => {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [token, setToken] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSendEmail = () => {
    if (!email) {
      alert('Email is required.');
      return;
    }

    // Simulate API call success
    setStep(2);
    alert('An email with a token has been sent to your email address.');
  };

  const handleVerifyToken = () => {
    if (!token) {
      alert('Token is required.');
      return;
    }

    // Simulate token verification success
    setStep(3);
  };

  const handleResetPassword = () => {
    if (!newPassword || !confirmPassword) {
      alert('Both password fields are required.');
      return;
    }

    if (newPassword !== confirmPassword) {
      alert('Passwords do not match.');
      return;
    }

    // Simulate password reset success
    alert('Password has been reset successfully.');
    setStep(1);
    setEmail('');
    setToken('');
    setNewPassword('');
    setConfirmPassword('');
  };

  return (
    <div className="containering">
      <div className="photo-section">
        <div className='pic-sec'></div>
      </div>
      <div className="content-section">
        <h1>Forgot Password?</h1>
        <p>No need to panic! Simply follow the steps below to reset your password and continue enjoying our services.<br />Your security is our priority.</p>
        <div className="details">
          <div className="container">
            {step === 1 && (
              <EmailStep email={email} setEmail={setEmail} handleSendEmail={handleSendEmail} />
            )}
            {step === 2 && (
              <TokenStep token={token} setToken={setToken} handleVerifyToken={handleVerifyToken} />
            )}
            {step === 3 && (
              <PasswordResetStep
                newPassword={newPassword}
                setNewPassword={setNewPassword}
                confirmPassword={confirmPassword}
                setConfirmPassword={setConfirmPassword}
                handleResetPassword={handleResetPassword}
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
