import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './ClaimList.css';

const ClaimList = () => {
  const [claims, setClaims] = useState([]);
  const [filteredClaims, setFilteredClaims] = useState([]);
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [remarks, setRemarks] = useState('');
  const [filter, setFilter] = useState('All'); // State to handle filter
  const history = useHistory();

  useEffect(() => {
    axios.get('http://localhost:8080/admin/claims', {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      setClaims(response.data);
      setFilteredClaims(response.data); // Initialize filtered claims with all claims
    })
    .catch(error => {
      console.error('There was an error fetching the claims!', error);
    });
  }, []);

  const handleClaimClick = (claim) => {
    setSelectedClaim(claim);
    setRemarks(claim.remarks || '');
  };

  const handleApproval = (claimId, isApproved, remarks) => {
    axios.post(`http://localhost:8080/admin/claims/${claimId}/approve?remarks=${remarks}&isApproved=${isApproved}`, {}, {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      // After successful approval/rejection, refresh claims
      setSelectedClaim(null);
      axios.get('http://localhost:8080/admin/claims', {
        headers: {
          'Authorization': 'Basic YWRtaW4xMjM6Y21z'
        }
      })
      .then(response => {
        setClaims(response.data);
        setFilteredClaims(response.data);
      });
    })
    .catch(error => {
      console.error('There was an error updating the claim status!', error);
    });
  };

  const handleFilterChange = (event) => {
    const selectedFilter = event.target.value;
    setFilter(selectedFilter);
    if (selectedFilter === 'All') {
      setFilteredClaims(claims);
    } else {
      const filtered = claims.filter(claim => {
        const status = claim.status === 'pending' ? 'Pending' : claim.status;
        return status === selectedFilter;
      });
      setFilteredClaims(filtered);
    }
  };

  const getStatusLabel = (status) => {
    switch(status) {
      case 'pending':
        return 'Pending';
      case 'APPROVED':
        return 'Approved';
      case 'REJECTED':
        return 'Rejected';
      default:
        return 'Unknown';
    }
  };

  return (
    <div>
      {!selectedClaim ? (
        <div className='hmm'>
          <table>
            <thead>
              <tr>
                <th>claimId</th>
                <th>Email</th>
                <th>Amount Claimed</th>
                <th>Policy Name</th>
                <th>{<select value={filter} onChange={handleFilterChange}>
              <option value="All">Status</option>
              <option value="Pending">Pending</option>
              <option value="APPROVED">Approved</option>
              <option value="REJECTED">Rejected</option>
            </select>}</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {filteredClaims.map(claim => (
                <tr key={claim.claimId} onClick={() => handleClaimClick(claim)} style={{ cursor: 'pointer' }}>
                  <td>{claim.claimId}</td>
                  <td>{claim.userEmail}</td>
                  <td>{claim.amountClaimed}</td>
                  <td>{claim.policyName}</td>
                  <td>{getStatusLabel(claim.status)}</td>
                  <td>
                    <Link to="#">View</Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div>
        <div className="claim-details">
        <div onClick={() => setSelectedClaim(null)} style={{ cursor: 'pointer' }}>
        <i class="fa-solclaimId fa-arrow-left"></i>
        </div>
          <p><strong>Email:<br/></strong> {selectedClaim.userEmail}</p>
          <p><strong>Amount Claimed:<br/></strong> {selectedClaim.amountClaimed}</p>
          <p><strong>Policy Name:<br/></strong> {selectedClaim.policyName}</p>
          <p><strong>Status:<br/></strong> {getStatusLabel(selectedClaim.status)}</p>
          <p><strong>Remarks:<br/></strong>
          {selectedClaim.status === 'pending' && ( <input
                                                    type="text"
                                                    placeholder={selectedClaim.adminRemarks}
                                                    value={remarks}
                                                    onChange={(e) => setRemarks(e.target.value)}
                                                    disabled={selectedClaim.status !== 'pending'}
                                                    />
)}
{selectedClaim.status !== 'pending' && (selectedClaim.adminRemarks)}
          </p>
        </div>

        {selectedClaim.status !== 'pending' && <div className='chotu'></div>}

<div className='claim-details-wrapper'>
<div className='claim-details-2'>
        <div>

{selectedClaim.status === 'pending' && (
  <div className='simp'>
    <h1>Claim Management Portal</h1>
    <p>Easily approve or reject claims at your fingertips.</p>
    <button onClick={() => handleApproval(selectedClaim.claimId, true, remarks)}>Approve</button>
    <button onClick={() => handleApproval(selectedClaim.id, false, remarks)}>Reject</button>
  </div>
)}
</div>
        </div>
</div>
        </div>
      )}
    </div>
  );
};

export default ClaimList;
