import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './PolicyList.css';

const PolicyList = () => {
  const [policies, setPolicies] = useState([]);
  const [expandedPolicyId, setExpandedPolicyId] = useState(null);
  const userEmail = localStorage.getItem('userEmail');

  useEffect(() => {
    axios.get('http://localhost:8080/admin/policies', {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      setPolicies(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the policies!', error);
    });
  }, []);

  const handleEnroll = (policyName) => {
    const registrationData = {
      userEmail: String(userEmail),
      policyName: String(policyName)
    };
    console.log(registrationData);

    axios.post('http://localhost:8090/enrollement/reg', { registrationData })
    .then(response => {
      console.log('Enrollment successful:', response.data,userEmail, policyName);
    })
    .catch(error => {
      console.error('There was an error enrolling in the policy!', error);
    });
  };

  const toggleDescription = (policyId) => {
    setExpandedPolicyId(expandedPolicyId === policyId ? null : policyId);
  };

  return (
    <div className='policy-list'>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Policy Name</th>
            <th>Coverage Amount</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {policies.map(policy => (
            <React.Fragment key={policy.id}>
              <tr onClick={() => toggleDescription(policy.id)} style={{ cursor: 'pointer' }}>
                <td>{policy.id}</td>
                <td>{policy.policyName}</td>
                <td>{policy.coverageAmount}</td>
                <td>
                  {expandedPolicyId === policy.id ? (
                    <div>
                      <p>{policy.description}</p>
                      <button onClick={() => handleEnroll(policy.policyName)}>Enroll</button>
                    </div>
                  ) : (
                    <span>
                      {policy.description.length > 100 ? 
                        `${policy.description.substring(0, 100)}... ` : policy.description}
                      <span className="more-link" onClick={() => toggleDescription(policy.id)}>
                      </span>
                    </span>
                  )}
                </td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PolicyList;
