import React, { useState } from 'react';
import './Reg.css';

function Reg() {
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [mobileNum, setMobileNum] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    if (!email || !firstName || !lastName || !mobileNum || !password) {
      alert('Please fill in all fields');
      return;
    }

    // Here, add your registration logic
    // Assign the employee role automatically

    // Simulating email verification and redirection
    alert('Registration successful! Please check your email for verification.');
    window.location.href = '/login';
  };

  return (
    <div className='bg'>
      <div className='wrapper'>
        <video src='/videos/video-7.mp4' autoPlay loop muted />
        <div className='dual'>
            <div className='bg-img'></div>
            <div className='wrapper1'>
            <form onSubmit={handleRegister}>
            <h1>Register</h1>
            <div className="input-box">
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="First Name"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  required
                />
                <input
                  type="text"
                  placeholder="Last Name"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">

              </div>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="Mobile Number"
                  value={mobileNum}
                  onChange={(e) => setMobileNum(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit">Register</button>
              <div className="login-link">
                <p>Already have an account? <a href="/login">Login</a></p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Reg;
