import React, { useState } from 'react';
import './Signin.css';

function Signin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState(''); // State for the selected role

  const handleLogin = (e) => {
    if (!username || !password || !role) {
      e.preventDefault();
      alert('Please fill in all fields');
      return;
    }

    // let redirectUrl = '#';

    // if (role === 'Employee') {
    //   redirectUrl = '#employee';
    // } else if (role === 'Hospital TPA') {
    //   redirectUrl = '#hospital-tpa';
    // } else if (role === 'Admin') {
    //   redirectUrl = '#admin';
    // }

    // window.location.href = redirectUrl;
  };

  return (
    <div className='bg'>
      <div className='wrapper'>
        <video src='/videos/video-7.mp4' autoPlay loop muted />
        <div className='dual'>
          <div className='bg-img'></div>
          {/* <div className='wrapper1'> */}
            <form onSubmit={(e) => e.preventDefault()}>
              <h1>Welcome Back!</h1>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="Email"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <select 

                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  required
                >
                  <option value='' disabled>Select Role</option>
                  <option value="Employee">Employee</option>
                  <option value="Hospital TPA">Hospital TPA</option>
                  <option value="Admin">Admin</option>
                </select>

              </div>


              <div className="remember-forgot">
                <a href="#">Forgot password?</a>
              </div>
              <a href={role ? `#${role}` : '#'} onClick={handleLogin}>
                <button type='button'>Login</button>
              </a>
              <div className="register-link">
                <p>Don't have an account?<a href="/sign-up">Register</a></p>
              </div>
            </form>
          {/* </div> */}
        </div>
      </div>
    </div>
  );
}

export default Signin;
