import React, { useEffect, useState } from 'react';
import './UserList.css'; // Import CSS for styling

const UserList = () => {
  const [claims, setClaims] = useState([]);
  const [expandedClaimId, setExpandedClaimId] = useState(null);

  useEffect(() => {
    const fetchClaims = async () => {
      const email = localStorage.getItem('userEmail'); // Retrieve email from local storage
      if (email) {
        try {
          const response = await fetch(`http://localhost:8090/claim/user/${email}`);
          if (response.ok) {
            const data = await response.json();
            // Sort the claims by claim ID in ascending order
            const sortedClaims = data.sort((a, b) => a.id - b.id);
            setClaims(sortedClaims); // Store the sorted array of claims
          } else {
            console.error('Failed to fetch claims:', response.statusText);
          }
        } catch (error) {
          console.error('Error fetching claims:', error);
        }
      } else {
        console.error('User email not found in local storage');
      }
    };
    fetchClaims();
  }, []);

  const handleExpand = (id) => {
    setExpandedClaimId(expandedClaimId === id ? null : id);
  };

  return (
    <div className='claim-list'>
      <table>
        <thead>
          <tr>
            <th>Claim ID</th>
            <th>Email</th>
            <th>Amount Claimed</th>
            <th>Status</th>
            <th>Admin Remarks</th>
          </tr>
        </thead>
        <tbody>
          {claims.map((claim) => (
            <React.Fragment key={claim.id}>
              <tr onClick={() => handleExpand(claim.id)} style={{ cursor: 'pointer' }}>
                <td>{claim.id}</td>
                <td>{claim.userEmail}</td>
                <td>â‚¹ {claim.amountClaimed.toFixed(2)}</td>
                <td>{claim.status}</td>
                <td>
                  {expandedClaimId === claim.id ? (
                    <div>
                      <p>{claim.adminRemarks}</p>
                    </div>
                  ) : (
                    <span>
                      {claim.adminRemarks.length > 100 ? 
                        `${claim.adminRemarks.substring(0, 100)}... ` : claim.adminRemarks}
                      <span className="more-link" onClick={() => handleExpand(claim.id)} />
                    </span>
                  )}
                </td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
