import React, { useEffect, useState } from 'react';
import './HosClaim.css';

const HosClaim = ({ claim, onBack }) => { // Accept claim and onBack as props
  const [userEmail, setEmail] = useState('');
  const [policyName, setpolicyName] = useState('');
  const [amountClaimed, setamountClaimed] = useState('');

  useEffect(() => {
    // Get email from local storage
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      setEmail(userEmail);
    }
    // Set policy name from the selected claim
    if (claim) {
      setpolicyName(claim.policyName);
    }
  }, [claim]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const claimData = {
      userEmail,
      policyName,
      amountClaimed,
    };

    try {
      const response = await fetch('http://localhost:8090/claim/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(claimData),
      });

      if (response.ok) {
        alert('Claim submitted successfully!');
        window.location.href = '/user';
      } else {
        alert('Failed to submit the claim. Please try again.');
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="claim-form-container-1">
      <button className="back-button" onClick={onBack}>Back</button> {/* Back button */}
      <form className="claim-form-1" onSubmit={handleSubmit}>
        <h2>Cash Your Bills!</h2>
        <input
          type="email"
          placeholder="Email"
          value={userEmail}
          onChange={(e) => setEmail(e.target.value)}
          disabled
          required
        />
        <input
          type="text"
          placeholder="Policy Number"
          value={policyName}
          onChange={(e) => setpolicyName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount Claimed"
          value={amountClaimed}
          onChange={(e) => setamountClaimed(e.target.value)}
          required
        />
        <button type="submit">Submit Claim</button>
      </form>
      <div className='claim-bg-0'></div>
      <div className='claim-bg-01'></div>
      <div className='claim-bg-02'></div>
    </div>
  );
};

export default HosClaim;
