import React, { useEffect, useState } from 'react';

const ClaimDetail = () => {
  const [claim, setClaim] = useState(null);
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    // Fetching the claim data
    const fetchClaim = async () => {
      try {
        const response = await get('http://localhost:8080/claim/1', {
          headers: {
            'Authorization': 'Basic VGVhbTE6VGVhbTE=',
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setClaim(data);
      } catch (error) {
        console.error('Error fetching claim:', error);
      }
    };

    fetchClaim();
  }, []);

  if (!claim) {
    return <div>Loading...</div>;
  }

  return (
    <div className="claim-detail">
      <h2>Claim Details</h2>
      <div className="claim-summary" onClick={() => setIsExpanded(!isExpanded)}>
        <h3>Claim ID: {claim.id}</h3>
        <p>Amount Claimed: â‚¹{claim.amountClaimed.toFixed(2)}</p>
        <p>Status: {claim.status}</p>
        <p>Claim Date: {claim.claimDate ? new Date(claim.claimDate).toLocaleDateString() : 'Not Provided'}</p>
      </div>

      {isExpanded && (
        <div className="admin-remarks">
          <h4>Admin Remarks:</h4>
          <p>{claim.adminRemarks}</p>
        </div>
      )}
    </div>
  );
};

export default ClaimDetail;
