import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './Emp_claim.css';

const Emp_claim = () => {
  const [userEmail, setuserEmail] = useState('');
  const [policyName, setpolicyName] = useState('');
  const [amountClaimed, setamountClaimed] = useState('');

  useEffect(() => {
    const storeduserEmail = localStorage.getItem('userEmail');
    if (storeduserEmail) {
      setuserEmail(storeduserEmail);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const claimData = {
      userEmail,
      policyName,
      amountClaimed
    };
    console.log(claimData);

    try {
      const response = await axios.post('http://localhost:8090/claim/submit', claimData, {
        headers: {
          'Content-Type': 'application/json',
        },
        withCredentials: true,
      });
      alert('Claim submitted successfully!');
      window.location.href = '/user';
    } catch (error) {
      console.error('There was an error submitting the claim!', error);
      alert('Failed to submit claim. Please try again.');
    }
  };

  return (
    <div className="claim-form-container">
      <a>_______________</a>
      <h2>Let's Claim!</h2>
      <form className="claim-form" onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={userEmail}
          onChange={(e) => userEmail(e.target.value)}
          disabled
          required
        />
        <input
          type="text"
          placeholder="Policy Name"
          value={policyName}
          onChange={(e) => setpolicyName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="amountClaimed"
          value={amountClaimed}
          onChange={(e) => setamountClaimed(e.target.value)}
          required
        />
        <button type="submit">Submit Claim</button>
      </form>
      <div className='claim-bg'>
        <p>Your healthcare expenses should never feel like a loss. With our reimbursement portal, easily turn your out-of-pocket costs into cash back. <br/><br/>Simply submit your claims through our user-friendly interface. We’re here to make the process quick and hassle-free!</p>
      </div>
      <div className='claim-bg-1'>
        <h1>Expense to Income:   <br/>Claim What’s Yours!</h1>
      </div>
    </div>
  );
};

export default Emp_claim;
