import React, { useState } from 'react';
import { Route, BrowserRouter as Router, Switch, useHistory } from 'react-router-dom';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [token, setToken] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [step, setStep] = useState(1); // Step 1: Enter Email, Step 2: Enter Token, Step 3: Reset Password
  const history = useHistory();

  const handleEmailSubmit = (e) => {
    e.preventDefault();
    // Simulate sending token to email
    alert('Token sent to email (simulation)');
    setStep(2);
  };

  const handleTokenSubmit = (e) => {
    e.preventDefault();
    // Simulate token verification
    if (token === '123456') { // For simulation, assume '123456' is the correct token
      setStep(3);
    } else {
      alert('Invalid token (simulation)');
    }
  };

  const handlePasswordReset = (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }
    // Simulate password reset
    alert('Password reset successfully (simulation)');
    history.push('/login');
  };

  return (
    <div>
      {step === 1 && (
        <form onSubmit={handleEmailSubmit}>
          <h2>Forgot Password</h2>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          <button type="submit">Send Token</button>
        </form>
      )}
      {step === 2 && (
        <form onSubmit={handleTokenSubmit}>
          <h2>Enter Token</h2>
          <label>Token:</label>
          <input type="text" value={token} onChange={(e) => setToken(e.target.value)} required />
          <button type="submit">Verify Token</button>
        </form>
      )}
      {step === 3 && (
        <form onSubmit={handlePasswordReset}>
          <h2>Reset Password</h2>
          <label>New Password:</label>
          <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} required />
          <label>Confirm Password:</label>
          <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
          <button type="submit">Reset Password</button>
        </form>
      )}
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Switch>
        <Route path="/forgot-password" component={ForgotPassword} />
        <Route path="/login" component={() => <div>Login Page</div>} />
        {/* Add other routes as needed */}
      </Switch>
    </Router>
  );
};

export default App;
