import axios from 'axios';
import React, { useState } from 'react';
import './Addpolicy.css';

function Addpolicy() {
  const [policyNumber, setPolicyNumber] = useState('');
  const [coverageAmount, setCoverageAmount] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const policyData = {
      policyNumber,
      coverageAmount,
      description,
    };

    try {
      const response = await axios.post('http://localhost:8080/admin/policies/add', policyData);
      console.log(policyData);

      if (response.status === 200) {
        alert('Policy added successfully');
        // Reset form fields
        setPolicyNumber('');
        setCoverageAmount('');
        setDescription('');
      } else {
        alert('An error occurred while adding the policy');
      }
    } catch (error) {
      alert('An error occurred: ' + error.message);
    }
  };

  return (
    <div className="adcontainer">
      <div className="form-container-policy">
        <h1>Add New Policies</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <input
              type="text"
              id="policyNumber"
              value={policyNumber}
              onChange={(e) => setPolicyNumber(e.target.value)}
              placeholder="Enter Policy Name"
              required
            />
          </div>
          <div className="input-box">
            <input
              type="text"
              id="coverageAmount"
              value={coverageAmount}
              onChange={(e) => setCoverageAmount(e.target.value)}
              placeholder="Enter Coverage Amount"
              required
            />
          </div>
          <div className="input-box">
            <input
              type="text"
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="About the Policy..."
              required
            />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default Addpolicy;
