import React, { useState } from 'react';
import './Forgt_ps.css'; // Ensure this is the correct path to your CSS file

const ForgotPassword = () => {
  const [step, setStep] = useState(1);
  const [email, setEmail] = useState('');
  const [token, setToken] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSendEmail = () => {
    // Simulate API call success
    setStep(2);
    setMessage('An email with a token has been sent to your email address.');
  };

  const handleVerifyToken = () => {
    // Simulate token verification success
    setStep(3);
    setMessage('');
  };

  const handleResetPassword = () => {
    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match.');
      return;
    }

    // Simulate password reset success
    setMessage('Password has been reset successfully.');
    setStep(1);
    setEmail('');
    setToken('');
    setNewPassword('');
    setConfirmPassword('');
  };

  return (
    <div className="container">
      {step === 1 && (
        <div>
          <h2>Forgot Password</h2>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button onClick={handleSendEmail}>Send Email</button>
        </div>
      )}
      {step === 2 && (
        <div>
          <h2>Verify Token</h2>
          <input
            type="text"
            placeholder="Enter the token"
            value={token}
            onChange={(e) => setToken(e.target.value)}
          />
          <button onClick={handleVerifyToken}>Verify Token</button>
        </div>
      )}
      {step === 3 && (
        <div>
          <h2>Reset Password</h2>
          <input
            type="password"
            placeholder="New password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <input
            type="password"
            placeholder="Confirm new password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <button onClick={handleResetPassword}>Reset Password</button>
        </div>
      )}
      {message && <p>{message}</p>}
    </div>
  );
};

export default ForgotPassword;
