import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';

const ClaimDetails = () => {
  const { id } = useParams();
  const history = useHistory();
  const [claim, setClaim] = useState({});
  const [status, setStatus] = useState('');
  const [remarks, setRemarks] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:8080/admin/claims/${id}`, {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      setClaim(response.data);
      setStatus(response.data.status);
      setRemarks(response.data.adminRemarks);
    })
    .catch(error => {
      console.error('There was an error fetching the claim!', error);
    });
  }, [id]);

  const handleUpdate = () => {
    axios.put(`API_URL/${id}`, {
      status: status,
      adminRemarks: remarks
    }, {
      headers: {
        'Authorization': ' Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(() => {
      history.push('/');
    })
    .catch(error => {
      console.error('There was an error updating the claim!', error);
    });
  };

  return (
    <div>
      <h1>Claim Detail</h1>
      <p>Email: {claim.userEmail}</p>
      <p>Amount Claimed: {claim.amountClaimed}</p>
      <p>Policy Name: {claim.policyName}</p>
      <div>
        <label>
          Status:
          <select value={status} onChange={(e) => setStatus(e.target.value)}>
            <option value="Approved">Approved</option>
            <option value="Denied">Denied</option>
          </select>
        </label>
      </div>
      <div>
        <label>
          Remarks:
          <textarea value={remarks} onChange={(e) => setRemarks(e.target.value)} />
        </label>
      </div>
      <button onClick={handleUpdate}>Update</button>
    </div>
  );
}

export default ClaimDetails;
