import React, { useState } from 'react';
import './Reg.css';

function Reg() {
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [mobileNum, setMobileNum] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleRegister = (e) => {
    e.preventDefault();
    if (!email || !firstName || !lastName || !mobileNum || !password || !confirmPassword) {
      alert('Please fill in all fields');
      return;
    }

    if (!/^\d{10}$/.test(mobileNum)) {
      alert('Please enter a valid 10-digit mobile number');
      return;
    }

    if (password !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    alert('Registration successful! Please check your email for verification.');
    window.location.href = '/login';
  };

  return (
    <div className='bg1'>
        <video src='/videos/video-7.mp4' autoPlay loop muted />
        <div className='dual'>
        <div className='bg-img'></div>

        <form onSubmit={handleRegister}>
        <h1>Register</h1>
          <div className="input-box-reg">
            <label>Email Address</label>
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-row-reg">
            <div className="input-box-reg">
              <label>First Name</label>
              <input
                type="text"
                placeholder="First Name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
              />
            </div>
            <div className="input-box-reg">
              <label>Last Name</label>
              <input
                type="text"
                placeholder="Last Name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
              />
            </div>
          </div>
          <div className="input-box-reg">
            <label>Mobile Number</label>
            <input
              type="text"
              placeholder="Mobile Number"
              value={mobileNum}
              onChange={(e) => setMobileNum(e.target.value)}
              required
            />
          </div>
          <div className="input-box-reg">
            <label>Password</label>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="input-box-reg">
            <label>Retype Password</label>
            <input
              type="password"
              placeholder="Retype Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="animated-button">Register</button>
          <div className="login-link">
            <p>Already have an account? <a href="/login">Login</a></p>
          </div>
        </form>
        </div>
    </div>
  );
}

export default Reg;
