import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './HosClaim.css';

const HosClaim = () => {
  const [email, setEmail] = useState('');
  const [Uemail, setUEmail] = useState('');
  const [policyNumber, setPolicyNumber] = useState('');
  const [amount, setAmount] = useState('');

  useEffect(() => {
    const storedEmail = localStorage.getItem('userEmail');
    if (storedEmail) {
      setEmail(storedEmail);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const claimData = {
      email,
      uemail,
      policyNumber,
      amount
    };
    console.log(claimData);

    try {
      const response = await axios.post('http://localhost:8090/claim/submit', claimData);
      alert('Claim submitted successfully!');
      window.location.href = '/user';
    } catch (error) {
      console.error('There was an error submitting the claim!', error);
      alert('Failed to submit claim. Please try again.');
    }
  };

  return (
    <div className="claim-form-container-1">
      <a>_______________</a>
      <h2>Easy Claim Process!</h2>
      <form className="claim-form-1" onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Hospital Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="User Email"
          value={Uemail}
          onChange={(e) => setUEmail(e.target.value)}
          disabled
          required
        />
        <input
          type="text"
          placeholder="Policy Name"
          value={policyNumber}
          onChange={(e) => setPolicyNumber(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit">Submit Claim</button>
      </form>
      <div className='claim-bg1'>
        <p>Ensure your patients receive the care they need without worrying about payment delays. Our cashless claim portal allows you to process claims effortlessly, right after treatment.<br/><br/>Submit claims quickly and easily through our intuitive interface. Weâ€™re committed to making the billing process smooth and stress-free!</p>
      </div>
      <div className='claim-bg1-1'>
        <h1>Care First, Pay Later..<br/>Simple Cashless Claims!</h1>
      </div>
    </div>
  );
};

export default HosClaim;
