import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './Admit_hos.css';

const Admit_hos = () => {
  const [email, setEmail] = useState('');
  const [policyNumber, setPolicyNumber] = useState('');
  const [policies, setPolicies] = useState([]);
  const [accEmail, setAccEmail] = useState('');

  // Fetch policies when email is entered
  useEffect(() => {
    if (email) {
      const fetchPolicies = async () => {
        try {
          const response = await axios.get(`http://localhost:8080/admin/check/${email}`, {
            headers: {
              'Content-Type': 'application/json',
              'Authorization': 'Basic YWRtaW4xMjM6Y21z',
            }
          });
          setPolicies(response.data.policyNames || []); // Assuming the response contains policyNames
        } catch (error) {
          console.error("Error fetching policies:", error);
        }
      };

      fetchPolicies();
    }
  }, [email]); // Trigger when email changes

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userEmail = localStorage.getItem('userEmail'); // Get email from local storage
    const policyName = policyNumber; // Use selected policy

    // Check if user exists for given email and policy name
    try {
      const response = await axios.get(`http://localhost:8080/admin/checkUser?email=${userEmail}&policyName=${policyName}`, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Basic YWRtaW4xMjM6Y21z',
        }
      });

      if (!response.data) {
        alert('User not found');
      } else {
        // Submit the claim
        const claimData = {
          userEmail,
          accEmail,
          policyName,
          amountClaimed: 0,
          status: 'Not Submitted',
          adminRemarks: 'Not Submitted'
        };

        await axios.post('http://localhost:8090/claim/submit', claimData);
        alert('Claim submitted successfully!');
        window.location.href = '/user'; // Redirect after submission
      }
    } catch (error) {
      console.error("Error during submission:", error);
    }
  };

  return (
    <div className="claim-form-container-2">
      <form className="claim-form-2" onSubmit={handleSubmit}>
        <h1>Verify Patient Details</h1>

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        {policies.length > 0 && (
          <select
            value={policyNumber}
            onChange={(e) => setPolicyNumber(e.target.value)}
            required
          >
            <option value="">Select Policy</option>
            {policies.map((policy, index) => (
              <option key={index} value={policy}>{policy}</option>
            ))}
          </select>
        )}

        <input
          type="email"
          placeholder="Account Email"
          value={accEmail}
          onChange={(e) => setAccEmail(e.target.value)}
          required
        />

        <button type="submit">Submit Claim</button>
      </form>
      <div className="side-2"></div>
      <div className="side-3"></div>
    </div>
  );
};

export default Admit_hos;
