import React, { useEffect, useState } from 'react';
import './HosClaim.css';

const HosClaim = () => {
  const [userEmail, setEmail] = useState('');
  const [policyName, setpolicyName] = useState('');
  const [amountClaimed, setamountClaimed] = useState('');

  useEffect(() => {
    // Get email from local storage
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      setEmail(userEmail);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const claimData = {
      userEmail,
      policyName,
      amountClaimed,
    };

    try {
      const response = await fetch('http://localhost:8090/claim/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(claimData),
      });

      if (response.ok) {
        alert('Claim submitted successfully!');
        window.location.href = '/user';
      } else {
        alert('Failed to submit the claim. Please try again.');
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="claim-form-container-1">
      <a>_______________</a>
      <h2>Easy Claim Process!</h2>
      <form className="claim-form-1" onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={userEmail}
          onChange={(e) => setEmail(e.target.value)}
          disabled
          required
        />
        <input
          type="text"
          placeholder="Policy Number"
          value={policyName}
          onChange={(e) => setpolicyName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="amountClaimed"
          value={amountClaimed}
          onChange={(e) => setamountClaimed(e.target.value)}
          required
        />
        <button type="submit">Submit Claim</button>
      </form>
      <div className='claim-bg1'>
        <p>
          Ensure your patients receive the care they need without worrying about payment delays. 
          Our cashless claim portal allows you to process claims effortlessly, right after treatment.<br/><br/>
          Submit claims quickly and easily through our intuitive interface. Weâ€™re committed to making the billing 
          process smooth and stress-free!
        </p>
      </div>
      <div className='claim-bg1-1'>
        <h1>Care First, Pay Later..<br/>Simple Cashless Claims!</h1>
      </div>
    </div>
  );
};

export default HosClaim;
