import axios from 'axios';
import React, { useState } from 'react';
import AdminDashboard from './AdminDashboard';
import './Customer.css';

function Customer() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showDashboard, setShowDashboard] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password || !role) {
      alert('Please fill in all fields');
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8080/login?email=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`);

      if (response.status === 200) {
        setIsLoggedIn(true);
        setTimeout(() => setShowDashboard(true), 1000); // delay to show dashboard after transition
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        alert('Invalid username or password');
      } else {
        alert('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div className='bg'>
      {!showDashboard ? (
        <div className={`wrapper ${isLoggedIn ? 'fade-to-black' : ''}`}>
          <video src='/videos/video-7.mp4' autoPlay loop muted />
          <div className='dual'>
            <div className='bg-img'></div>
            <div className={`wrapper1 ${isLoggedIn ? 'expand' : ''}`}>
              <form onSubmit={handleLogin} className={`loginform ${isLoggedIn ? 'fade-away' : ''}`}>
                <h1>Welcome Back!</h1>
                <div className="input-box">
                  <input
                    type="text"
                    placeholder="Email"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </div>
                <div className="input-box">
                  <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className="input-box">
                  <select 
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                  >
                    <option value='' disabled>Select Role</option>
                    <option value="Customer">Customer</option>
                    <option value="Hospital TPA">Hospital TPA</option>
                    <option value="Admin">Admin</option>
                  </select>
                </div>
                <div className="remember-forgot">
                  <a href="/forgot">Forgot password?</a>
                </div>
                <button type='submit'>Login</button>
                <div className="register-link">
                  <p>Don't have an account?<a href="/sign-up">Register</a></p>
                </div>
              </form>
            </div>
          </div>
        </div>
      ) : (
        <><AdminDashboard/></>
      )}
    </div>
  );
}

export default Customer;
