import React, { useState } from 'react';
import './Samp.css';

function Samp() {
  const [hospitalid, setHospitalid] = useState('');
  const [hospitalname, setHospitalname] = useState('');
  const [isHospitalFormVisible, setIsHospitalFormVisible] = useState(true);

  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [mobileNum, setMobileNum] = useState('');

  const handleHospitalFormSubmit = (e) => {
    e.preventDefault();
    setIsHospitalFormVisible(false);
  };

  const handleRegisterFormSubmit = (e) => {
    e.preventDefault();
    // Handle the registration form submission logic here
    console.log({ email, firstName, lastName, mobileNum });
  };

  return (
    <div className='sbg'>
      {isHospitalFormVisible ? (
        <div className='text-wrap'>
          <h1>Link new hospitals to <br />the network!</h1>
          <form className="hospital-form" onSubmit={handleHospitalFormSubmit}>
            <div className="form-wrap">
              <input
                type="text"
                placeholder="Hospital ID"
                value={hospitalid}
                onChange={(e) => setHospitalid(e.target.value)}
                required
              />
            </div>
            <div className="form-wrap">
              <input
                type="text"
                placeholder="Hospital Name"
                value={hospitalname}
                onChange={(e) => setHospitalname(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="submit-button">Add Hospital</button>
          </form>
        </div>
      ) : (
        <div className='registration-form'>
          <form onSubmit={handleRegisterFormSubmit}>
            <div className="input-box-reg">
              <label>Email Address</label>
              <input
                type="email"
                placeholder="Email Address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="input-row-reg">
              <div className="input-box-reg">
                <label>First Name</label>
                <input
                  type="text"
                  placeholder="First Name"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  required
                />
              </div>
              <div className="input-box-reg">
                <label>Last Name</label>
                <input
                  type="text"
                  placeholder="Last Name"
                  value={lastName}
                  onChange={(e) => setLastName(e.target.value)}
                  required
                />
              </div>
            </div>
            <div className="input-box-reg">
              <label>Mobile Number</label>
              <input
                type="text"
                placeholder="Mobile Number"
                value={mobileNum}
                onChange={(e) => setMobileNum(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="animated-button">Register</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default Samp;
