import axios from 'axios';
import React, { useState } from 'react';
import './Emp.css';

function Emp() {
  const [nomineename, setNomineeName] = useState('');
  const [relation, setRelation] = useState('');

  const handleRelationChange = (event) => {
    setRelation(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const userEmail = localStorage.getItem('userEmail');

    if (!userEmail) {
      alert('User email not found in local storage.');
      return;
    }

    const nomineeData = {
      nomineename,
      relation,
    };
    console.log(nomineeData);
    console.log('User Email:', userEmail);

    try {
      const response = await axios.post(`http://localhost:8081/nominee/${userEmail}/add`, nomineeData);

      if (response.status === 201) {
        alert('Nominee added successfully.');
      } else {
        alert('Failed to add nominee.');
      }
    } catch (error) {
      console.error('Error adding nominee:', error);
      alert('An error occurred. Please try again.');
    }
  };

  return (
    <div className="nominee-container">
      <div className='wrap'>
        <h1>Secure Your Loved Ones</h1>
        <h2>Add family members to ensure their well-being under our comprehensive health policy. Provide them with the peace of mind and robust protection they deserve, ensuring a healthy and happy life for all your cherished ones. <br /><br />Together, let’s build a healthier tomorrow for you and your family, safeguarding what matters most.</h2>
      </div>
      <div className="nm-box nm-box1">
        <video src='/videos/video-11.mp4' autoPlay loop muted />
      </div>
      <div className="nm-box nm-box2">
        <form className="nominee-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              placeholder="Nominee Name"
              value={nomineename}
              onChange={(e) => setNomineeName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <select id="relation" value={relation} onChange={handleRelationChange}>
              <option value="" disabled>Select Relation</option>
              <option value="Father">Father</option>
              <option value="Mother">Mother</option>
              <option value="Spouse">Spouse</option>
              <option value="Child">Child</option>
            </select>
          </div>
          <button type="submit" className="submit-button">Add Nominee</button>
        </form>
      </div>
      <div className="nm-box nm-box3"></div>
      <div className="nm-box nm-box4"></div>
      <div className="nm-box nm-box5">
        <div className='gdknw'>
          <h2>Eligibility: </h2>
          <h3>Immediate family members such as spouse, children, parents, and in-laws can be added as nominees.
            Nominees must meet specific criteria outlined in the policy terms.</h3>
        </div>
      </div>
    </div>
  );
}

export default Emp;
