import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './AdminDashboard.css';
import './CustomerDashboard.css';

const CustomerDashboard = () => {
    const [policies, setPolicies] = useState([]);
    const userEmail = localStorage.getItem('userEmail');

    useEffect(() => {
        if (userEmail) {
            // API call to fetch the policy data
            axios.get(`http://localhost:8080/admin/check/${userEmail}`, {
                headers: {
                    'Authorization': 'Basic YWRtaW4xMjM6Y21z'
                }
            })
            .then(response => {
                setPolicies(response.data);
            })
            .catch(error => {
                console.error('Error fetching policies:', error);
            });
        }
    }, [userEmail]);

    return (
        <div className="dashboard-container">
            <div className="policy-list-1">
                <h2>Your Policies</h2>
                <div className="policy-cards">
                    {policies.map((policy) => (
                        <div key={policy.id} className="policy-card">
                            <h4>{policy.policyName}</h4>
                            <p>Remaining Amount: â‚¹{policy.amountRemaining.toFixed(2)}</p>
                        </div>
                    ))}
                </div>
            </div>
            <div className="functions">
                <div className="function-card-1">
                    <Link to="/userclaim" className="btn">
                        <img src="./images/7.png" className="admin-icon" alt="Submit Claims" />
                        <h3>Submit Claims</h3>
                        <p>Submit a new claim request with relevant details for processing</p>
                    </Link>
                </div>
                <div className="function-card-1">
                    <Link to="/policylist" className="btn">
                        <img src="./images/5.png" className="admin-icon" alt="Enroll Policy" />
                        <h3>Enroll Policy</h3>
                        <p>Register for a new policy to receive health coverage benefits</p>
                    </Link>
                </div>
                <div className="function-card-1">
                    <Link to="/user" className="btn">
                        <img src="./images/6.png" className="admin-icon" alt="View Claims" />
                        <h3>View Claims</h3>
                        <p>View claim history and check the current status of submitted claims</p>
                    </Link>
                </div>
                <div className="function-card-1">
                    <Link to="/emply" className="btn">
                        <img src="./images/1.png" className="admin-icon" alt="Add Nominee" />
                        <h3>Add Nominee</h3>
                        <p>Ensure your nominee benefits from your medical policy coverage</p>
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default CustomerDashboard;
