import axios from 'axios';
import React, { useState } from 'react';

function Samp() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [status, setStatus] = useState(null);

    const handleSubmit = async (event) => {
      event.preventDefault();

      try {
          const response = await axios.post('localhost:8080/login', {
              email,
              password
          });

          setStatus(response.status); // Set the response status
          setMessage(response.data.message); // Assuming the response contains a 'message' field
      } catch (error) {
          console.error('Error:', error);
          setStatus(error.response?.status); // Get the status from the error response or default to 500

      }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)}   
/>
          <br />
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <br />
          <button type="submit">Login</button>   

          <p>Status: {status}</p>
          <p>{message}</p>
      </form>
    );
}

export default Samp;
