import axios from 'axios';
import React, { useState } from 'react';

function Samp() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');   

  const [status, setStatus] = useState();   
// Store the response status
  const [error, setError] = useState(); // Store the error message

  const handleSubmit = async (event) => {
      event.preventDefault();

      try {
          console.log("line 17")
          console.log(email,password)
          var _json = {
            "email":"persondemo3321@gmail.com",
            "password":"ssdd1122"
        };
          const response = await axios.get('http://localhost:8080/login',JSON.stringify(_json));
          console.log("------")
          console.log(response)
          setStatus(response.status); // Set the response status
          setMessage(response.data.message); // Assuming the response contains a 'message' field
          setError(null); // Clear any previous error
      } catch (error) {
          console.error('Error:', error);
          setStatus(error.response?.status); // Get the status from the error response or default to 500
          setError(error.response?.data?.message || error.message); // Get the error message from the response or the error itself
      }
  };

  return (
      <form onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)}   
/>
          <br />
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <br />
          <button type="submit">Login</button>   

          <p>Status: {status}</p>
          <p>{message}</p>
          {error && <p>Error: {error}</p>}
      </form>
  );
}

export default Samp;
