import axios from 'axios';
import React, { useEffect, useState } from 'react';
// import './PolicyList.css';

const PolicyList = () => {
  const [policies, setPolicies] = useState([]);
  const [expandedPolicy, setExpandedPolicy] = useState(null);
  const email = localStorage.getItem('email');

  useEffect(() => {
    axios.get('http://localhost:8080/admin/policies', {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      setPolicies(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the policies!', error);
    });
  }, []);

  const handlePolicyClick = (policyId) => {
    setExpandedPolicy(policyId === expandedPolicy ? null : policyId);
  };

  const handleEnroll = (policyName) => {
    axios.post('##api paste here##', { policyName, email }, {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      console.log('Enrollment successful:', response.data);
    })
    .catch(error => {
      console.error('There was an error enrolling in the policy!', error);
    });
  };

  return (
    <div className='policy-list'>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Policy Name</th>
            <th>Coverage Amount</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {policies.map(policy => (
            <React.Fragment key={policy.id}>
              <tr onClick={() => handlePolicyClick(policy.id)} style={{ cursor: 'pointer' }}>
                <td>{policy.id}</td>
                <td>{policy.policyName}</td>
                <td>{policy.coverageAmount}</td>
                <td>
                  {expandedPolicy === policy.id ? (
                    <div>
                      <p>{policy.description}</p>
                      <button onClick={() => handleEnroll(policy.policyName)}>Enroll</button>
                    </div>
                  ) : (
                    <span>Click to see description</span>
                  )}
                </td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PolicyList;
