import React, { useEffect, useState } from 'react';
import './UserList.css'; // Import CSS for styling

const UserList = () => {
  const [claims, setClaims] = useState([]);
  const [expandedClaimId, setExpandedClaimId] = useState(null);
  console.log(`http://localhost:8090/claim/user/${email}`);

  useEffect(() => {
    const fetchClaims = async () => {
      const email = localStorage.getItem('userEmail'); // Retrieve email from local storage
      if (email) {
        const response = await fetch(`http://localhost:8090/claim/user/${email}`);
        if (response.ok) {
          const data = await response.json();
          console.log(email);
          setClaims(data); // Store the array of claims directly
        } else {
          console.error('Failed to fetch claims:', response.statusText);
        }
      } else {
        console.error('User email not found in local storage');
      }
    };
    fetchClaims();
  }, []);

  const handleExpand = (id) => {
    setExpandedClaimId(expandedClaimId === id ? null : id);
  };

  return (
<div className='class-list-wrapper'>
<div className="claim-view-container">
      <h1>Your Submitted Claims</h1>
      <div className="claim-list">
        {claims.map((claim) => (
          <div 
            key={claim.id} 
            className="claim-card" 
            onClick={() => handleExpand(claim.id)}
          >
            <h2>Claim ID: {claim.id}</h2>
            <p><strong>Email:</strong> {claim.userEmail}</p>
            <p><strong>Amount Claimed:</strong> ${claim.amountClaimed.toFixed(2)}</p>
            <p><strong>Status:</strong> {claim.status}</p>
            {expandedClaimId === claim.id && (
              <div className="admin-remarks">
                <p><strong>Admin Remarks:</strong> {claim.adminRemarks}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
</div>
  );
};

export default UserList;
