import React, { useEffect, useState } from 'react';
import './Example.css'; // Import CSS for styling

const ModernUserClaims = () => {
  const [claims, setClaims] = useState([]);
  const [filteredClaims, setFilteredClaims] = useState([]);
  const [expandedClaimId, setExpandedClaimId] = useState(null);
  const [filter, setFilter] = useState('All'); // State to handle filter
  const [showForm, setShowForm] = useState(false);
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [userEmail, setEmail] = useState('');
  const [policyName, setPolicyName] = useState('');
  const [amountClaimed, setAmountClaimed] = useState('');

  useEffect(() => {
    const fetchClaims = async () => {
      const email = localStorage.getItem('userEmail'); // Retrieve email from local storage
      if (email) {
        const response = await fetch(`http://localhost:8090/claim/user/${email}`);
        if (response.ok) {
          const data = await response.json();
          // Filter and sort claims
          const sortedData = data.sort((a, b) => a.id - b.id);
          setClaims(sortedData);
          setFilteredClaims(sortedData);
        } else if (response.status === 404) {
          alert("Sorry, no records found");
          window.location.href = '/customerdash';
        } else {
          console.error('Failed to fetch claims:', response.statusText);
        }
      } else {
        console.error('User email not found in local storage');
      }
    };
    fetchClaims();
  }, []);

  useEffect(() => {
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      setEmail(userEmail);
    }
  }, []);

  const handleExpand = (claim) => {
    setExpandedClaimId(expandedClaimId === claim.id ? null : claim.id);
    setSelectedClaim(claim);
    setShowForm(false);
  };

  const handleFilterChange = (event) => {
    const selectedFilter = event.target.value;
    setFilter(selectedFilter);
    if (selectedFilter === 'All') {
      setFilteredClaims(claims);
    } else {
      const filtered = claims.filter(claim => claim.status === selectedFilter);
      setFilteredClaims(filtered);
    }
  };

  const handleFormOpen = () => {
    setShowForm(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const claimData = {
      userEmail,
      policyName,
      amountClaimed,
    };

    try {
      const response = await fetch('http://localhost:8090/claim/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(claimData),
      });

      if (response.ok) {
        alert('Claim submitted successfully!');
        window.location.href = '/user';
      } else {
        alert('Failed to submit the claim. Please try again.');
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
      console.error('Error:', error);
    }
  };

  const getStatusLabel = (status) => {
    switch (status) {
      case 'Pending':
        return 'Pending';
      case 'APPROVED':
        return 'Approved';
      case 'REJECTED':
        return 'Rejected';
      case 'Not Submitted':
        return 'Not Submitted';
      default:
        return 'Unknown';
    }
  };

  return (
    <div className='modern-claim-list'>
      <table>
        <thead>
          <tr>
            <th>Claim ID</th>
            <th>Email</th>
            <th>Amount Claimed</th>
            <th>
              <select value={filter} onChange={handleFilterChange}>
                <option value="All">Status</option>
                <option value="Pending">Pending</option>
                <option value="APPROVED">Approved</option>
                <option value="REJECTED">Rejected</option>
                <option value="Not Submitted">Not Submitted</option>
              </select>
            </th>
            <th>Admin Remarks</th>
          </tr>
        </thead>
        <tbody>
          {filteredClaims.map((claim) => (
            <React.Fragment key={claim.id}>
              <tr onClick={() => handleExpand(claim)} style={{ cursor: 'pointer' }}>
                <td>{claim.id}</td>
                <td>{claim.userEmail}</td>
                <td>₹ {claim.amountClaimed.toFixed(2)}</td>
                <td>{getStatusLabel(claim.status)}</td>
                <td>
                  {expandedClaimId === claim.id ? (
                    <div>
                      <p>{claim.adminRemarks}</p>
                      <button onClick={handleFormOpen}>Edit Claim</button>
                    </div>
                  ) : (
                    <span>
                      {claim.adminRemarks.length > 100
                        ? `${claim.adminRemarks.substring(0, 100)}... `
                        : claim.adminRemarks}
                      <span className="more-link" onClick={() => handleExpand(claim)}></span>
                    </span>
                  )}
                </td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
      {showForm && (
        <form className="modern-claim-form" onSubmit={handleSubmit}>
          <h2>Cash Your Bills!</h2>
          <input
            type="email"
            placeholder="Email"
            value={userEmail}
            onChange={(e) => setEmail(e.target.value)}
            disabled
            required
          />
          <input
            type="text"
            placeholder="Policy Number"
            value={policyName}
            onChange={(e) => setPolicyName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Amount Claimed"
            value={amountClaimed}
            onChange={(e) => setAmountClaimed(e.target.value)}
            required
          />
          <button type="submit">Submit Claim</button>
        </form>
      )}
    </div>
  );
};

export default ModernUserClaims;
