import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './HospitalList.css';

const HospitalList = () => {
  const [hospitals, setHospitals] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/admin/hospitals', {
      headers: {
        'Authorization': 'Basic YWRtaW4xMjM6Y21z'
      }
    })
    .then(response => {
      setHospitals(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the hospitals!', error);
    });
  }, []);

  return (
<div className='outershell'>
<div className='hospital-list'>
    <h1>Paperless Healthcare Providers</h1>
    <p>Discover a list of hospitals within the Mediclaim network that offer cashless and paperless treatment options, ensuring a smooth and efficient healthcare experience.Enjoy the benefits of immediate access to care while eliminating the hassle of upfront payments and paperwork</p>
      {hospitals.map(hospital => (
        <div className='hospital-card' key={hospital.id}>
          <div className='hospital-name'>{hospital.hospitalName}</div>
          <div className='hospital-address'>{hospital.address}</div>
        </div>
      ))}
    </div>
</div>
  );
};

export default HospitalList;
