import axios from 'axios';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Customer.css';

function Customer() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const history = useHistory();


  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password || !role) {
      alert('Please fill in all fields');
      const PasswordToggle = () => {
        const [password, setPassword] = useState('');
        const [isPasswordVisible, setIsPasswordVisible] = useState(false);

        const togglePasswordVisibility = () => {
          setIsPasswordVisible(!isPasswordVisible);
        };

    return;
    }

    if (role === 'Admin') {
      if (username === 'admin123' && password === 'cms') {
        setIsLoggedIn(true);
        setTimeout(() => history.push('/admin'), 1000); // delay to navigate after transition
      } else {
        alert('Invalid admin credentials');
      }
    } else {
      try {
        const response = await axios.get(`http://localhost:8081/login?email=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`);

        if (response.status === 200) {
          localStorage.setItem('userEmail', username);
          if (role === 'Customer') {
            setTimeout(() => history.push('/customerdash'), 1000); // delay to navigate after transition
          } else if (role === 'Hospital TPA') {
            setTimeout(() => history.push('/tpa'), 1000); // delay to navigate after transition
          }
        }
      } catch (error) {
        if (error.response && error.response.status === 401) {
          alert('Invalid username or password');
        } else {
          alert('An error occurred. Please try again.');
        }
      }
    }
  };

  return (
    <div className='bg'>
            <form onSubmit={handleLogin} className={`loginform`}>
              <h1>Login</h1>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="Email"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <input
                  type={isPasswordVisible ? 'text' : 'password'}
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="input-box">
                <select 
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  required
                  className="custom-select"
                >
                  <option value='' disabled>Select Role</option>
                  <option value="Customer">Customer</option>
                  <option value="Hospital TPA">Hospital TPA</option>
                  <option value="Admin">Admin</option>
                </select>
              </div>
              <div className="remember-forgot">
                <a href="/forgot">Forgot password?</a>
              </div>
              <button type='submit'>Login</button>
              <div className="register-link">
                <p>Don't have an account?<a href="/sign-up">Register</a></p>
              </div>
            </form>
        <div className='design-component'>
        </div>
      </div>
  );
}}

export default Customer;
